tinymce.PluginManager.add('1medialist', function(editor, url) {
	// Add a button that opens a window
	editor.addButton('1medialist', {
		text: 'Medien',
		icon: false,
		onclick: function() {
			// Open window
			editor.windowManager.open({
				title: 'Medienverwaltung',
				width: 800,
				height: 600,
				body: [
					{
						type   : 'button',
						name   : 'button',
						label  : 'label',

						icon : "/media/uploads/1_58ff4c85adbc6.png",
						minHeight: 100,
						// 						text   : '<img src="/media/uploads/1_58ff4c85adbc6.png">',
						onclick: function(e) {alert('Click')}
					},
					{
						type   : 'container',
						label  : 'HTML',
						style  : 'display: inline-block; max-width: 80px; width:80px !important; height: 80px;',
						html   : '<img src="/media/uploads/1_58ff4c85adbc6.png" class="cls1medialist">'
					},
					{
						type   : 'container',
						label  : 'HTML',
						style  : 'display: inline-block; max-width: 80px; width:80px !important; height: 80px;',
						html   : '<img src="/media/uploads/1_58ff4c85adbc6.png" class="cls1medialist">'
					},
					{type: 'radio', name: 'title', label: '1', value: '1', style : "background-image: url(/media/uploads/1_58ff4c85adbc6.png) no-repeat center center fixed; 	-webkit-background-size: cover;	-moz-background-size: cover;	-o-background-size: cover;	background-size: cover;	height:80px; width:80px;",},
					{type: 'radio', name: 'title', label: '2', value: '2'},
					{type: 'radio', name: 'title', label: '3', value: '3'}
				],
				onsubmit: function(e) {
					// Insert content when the window form is submitted
					editor.insertContent('MediaID: ' + e.data.title);
				}
			});
		}
	});

	// Adds a menu item to the tools menu
	editor.addMenuItem('1medialist', {
		text: 'Medienverwaltung',
		context: 'insert',
		onclick: function() {
			// Open window with a specific url
			editor.windowManager.open({
				title: 'Medienverwaltung',
				url: 'http://www.tinymce.com',
				width: 800,
				height: 600,
				buttons: [{
					text: 'Close',
					onclick: 'close'
				}]
			});
		}
	});
});